INSERT INTO `search_paper_detail` VALUES (621, '--', '赵燕伟;洪欢欢;王万良;姜高超;陆亿红;张仁贡;王欢', '', '一种基于可拓传导效应的区域水能承载能力计算方法', '2012.6.12, 中国, 中华人民中华人民共和国国家知识产权局, 201210197110.2', 'UPQ2oUMJesA');
