INSERT INTO `search_paper_detail` VALUES (623, '--', '刘盛;陈胜勇;汪晓妍;刘大伟;王万良', '', '基于二次曲线拟合的旋转立体视觉旋转轴的确定方法', '2012.3.22, 中国, 中华人民中华人民共和国国家知识产权局, 201210078579.4', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (624, '--', '胡峰俊;陈建;王万良;金亦挺;赵燕伟', '', '一种基于双目视觉的鞋底喷胶厚度控制方法', '2012.11.26, 中国, 中华人民中华人民共和国国家知识产权局, 201210489280.8', 'UPQ2oUMJesA');
