INSERT INTO `search_paper_detail` VALUES (626, '--', '蒋一波;郑建炜;王万良;陈琼;王雨晨', '', '一种小水电IED网中Zigbee协议与GOOSE协议的数据转换方法', '2012.11.29, 中国, 中华人民中华人民共和国国家知识产权局, 201210506750.7', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (627, '针对动态多变环境下的作业车间调度问题,在基于周期和事件驱动的滚动窗口再调度策略基础上,采用冻结时段思想,同时又允许再调度在冻结时段内的资源上的闲置时段安排任务,并由此建立了机器存在不可用时段约束的动态调度模型。借鉴量子进化算法中量子旋转门更新量子位的方法,设计了三段式交叉操作,并在算法框架中嵌入了局部搜索操作,提出了混合差分进化算法作为再调度的优化方法。通过算法性能与作业车间动态调度实验,验证了所提动态调度方法的有效性。', '王磊;王万良;王海燕;徐新黎;赵燕伟', '作业车间;动态调度;差分进化算法;滚动窗口;冻结时段', '基于混合差分进化算法的作业车间动态调度', '计算机集成制造系统, (03), 2012.3.15', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (628, '基于工序排序和机器分配的粒子编码方式,提出一种新的粒子位置更新方式,该方式使得粒子群算法更新可以直接在离散域执行.通过对工件工序进行多次机器分配来扩大搜索范围,引入改进的模拟退火算法,用以增强粒子群算法的邻域搜索能力,实现全局搜索与局部搜索能力的有效平衡.最后通过数值算例以及某电声企业纸盆车间批量调度的应用实例验证了所提出算法的有效性和可行性.', '张静;王万良;徐新黎;王海燕', '粒子群算法;柔性作业车间调度;批量调度;模拟退火', '基于改进粒子群算法求解柔性作业车间批量调度问题', '控制与决策, (04), 2012.4.15', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (629, '柔性作业车间调度问题是生产管理领域和组合优化领域的重要分支.本文提出一种基于Pareto支配的混合粒子群优化算法求解多目标柔性作业车间调度问题.首先采用基于工序排序和机器分配的粒子表达方式,并直接在离散域进行位置更新.其次,提出基于Baldwinian学习策略和模拟退火技术相结合的多目标局部搜索策略,以平衡算法的全局探索能力和局部开发能力.然后引入Pareto支配的概念来比较粒子的优劣性,并采用外部档案保存进化过程中的非支配解.最后用于求解该类问题的经典算例,并与已有算法进行比较,所提算法在收敛性和分布均匀性方面均具有明显优势.', '介婧;张静;王万良;徐新黎', '粒子群;多目标优化;柔性作业车间调度问题;Baldwinian学习策略', '混合粒子群算法求解多目标柔性作业车间调调度度问题', '控制理论与应用, (06), 2012.6.15', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (630, '子空间特征提取是人脸识别中的关键技术之一,结合局部Fisher判别分析技术和张量子空间分析技术的优点,本文提出了一种新的张量局部Fisher判别分析(Tensor local Fisher discriminant analysis,TLFDA)子空间降维技术.首先,通过对局部Fisher判别技术进行分析,调整了其类间散度目标泛函,使算法的识别性能更高且时间复杂度更低;其次,引入张量型降维技术对输入数据进行双边投影变换而非单边投影,获得了更高的数据压缩率;最后,采用迭代更新的方法计算最优的变换矩阵.通过ORL和PIE两个人脸库验证了所提算法的有效性.', '郑建炜;王万良;姚晓敏;石海燕', '人脸识别; Fisher判别分析; 维数约简; 局部结构保持; 判别信息 Face recognition,Fisher discriminant analysis,dimensionality reduction,local structure preservation,discriminant information', '张量局部Fisher判别分析的人脸识别', '自动化学报, 38(09), pp 1485-1495, 2012', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (631, '--', '姚信威;王万良;张小玮;岑跃峰', '', '无线网络中基于流媒体传输的自适应TFRC机制', '小型微型计算机系统, 33(3), pp 138-142, 2012', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (632, '--', '邱虹;蒋一波;郑建炜;王万良', '', '判别随机近邻嵌入分析方法', '计算机辅助设计与图形学学报, 24(11), pp 1477-1484, 2012', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (633, '--', '徐锦才;姚岳来;董大富;陈晓健;赵燕伟;陈胜勇;王万良;罗云霞;蒋一波;裘江海;程夏蕾;陈烨兴;陆亿红', '', '小水电站网络化控制与优化运行关键共性技术及推广应用', '浙江省科技厅, 2011浙江省科学技术奖, 一等奖, 2012.5.2', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (634, '--', '徐新黎;张景玲;姚信威;岑跃峰;赵燕伟;王万良;蒋一波;郑建炜;陆亿红', '', '网络化智能控制与调度方法及其应用', '中国人工智能学会, 吴文俊人工智能科学技术进步奖, 一等奖, 2012.11.1', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (635, '--', '姚信威;王万良;赵王军;王峥;应皇城', '', '基于无线传感器网络的室内定位软件', '2012.2.25, 中国, 中华人民中华人民共和国国家知识产权局, 2012SR055200', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (636, '考虑到实际生产中产品多、批量小的特点,建立了一种带工艺约束的并行机批处理调度优化模型。为解决调度中的分批问题,提出了一种新的基于产品需求量的批量划分方案及批量染色体编码方式,采用两级差分进化算法来解决批量划分和批次调度问题;针对标准差分进化算法收敛速度慢、易出现早熟现象等问题,引入动态随机搜索和随机变异的局部搜索策略,以增强标准差分进化算法的局部搜索能力。测试算例及调度实例的仿真结果表明,该算法能有效地提高算法收敛速度,平衡其全局搜索和局部探索能力。', '王万良;徐新黎;范丽霞;张静', '差分进化算法;动态随机搜索;局部搜索;并行机调度;批处理', '基于混合差分进化算法的并行机批处理调度问题研究', '机电工程, (02), pp 531-539, 2012.2.20', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (637, '--', '王万良;陈旻昊;郑建炜;黄琼芳', '', '一种增量式类内局部保持降维算法', '计算机科学, 11, pp 154-158, 2012', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (638, '--', '姚信威;李笠;岑跃峰;吴腾超;赵燕伟;王万良', '', '基于移动互联网的汽车远程控制与管理系统', '2012.10.22, 中国, 中华人民中华人民共和国国家知识产权局, 201210406970.2', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (639, '--', '姚信威;岑跃峰;赵燕伟;王万良;顾煕仁;陶砾', '', '一种IEEE 802.11网络的时隙分配方法', '2012.2.22, 中国, 中华人民中华人民共和国国家知识产权局, 201210040964.x', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (640, '针对产品研发过程中了解用户意象的需求,采用交互式遗传算法对产品设计方案实施优化。由于评价噪声影响用户意象的准确获取,对交互式遗传算法的用户评价噪声来源及其与评价精度之间的关系作了分析,对四种不同评价精度下的收敛速度、评价耗时和用户疲劳等指标进行了实验研究。结合产品优化设计的具体案例,论证了低精度评价方法具有降低疲劳、缩短评价时间、提高种群规模等优势,并且可以有效利用数理统计方法进行适应值预测和用户意向分析。', '赵燕伟;王万良;刘肖健;孙艳', '交互式遗传算法;评价疲劳;精度;用户意象;产品设计', '产品研发中面向用户意象的交互式遗传算法评价模式', '计算机集成制造系统, (02), pp 276-281, 2012.2.15', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (641, '--', '岑跃峰;赵燕伟;刘琳琅;王万良;蒋一波;郑建炜', '', '一种无线网络中改进的TCPW拥塞控制方法', '2012.5.25, 中国, 中华人民中华人民共和国国家知识产权局, 201210171762.9', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (642, '--', '徐新黎;姚信威;张科娜;赵燕伟;王万良;顾熙仁', '', '一种无线局域网的语音质量测定方法', '2012.5.25, 中国, 中华人民中华人民共和国国家知识产权局, 201210172045.8', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (643, '无线多媒体传输中,正确评估IEEE802.11e无线局域网的语音质量尤为重要.提出一种IEEE802.11e无线局域网的语音质量评价模型.该模型引入E-Model VoIP语音质量评估方法,估计IEEE802.11e无线局域网的语音质量.重点考虑碰撞丢失对语音质量的影响,建立新的碰撞概率表达式.通过MATLAB、NS2仿真分析比较了不同碰撞概率在新模型下的语音质量.结果表明提出的碰撞概率在新模型下语音质量最接近真实值,具有最高的评估准确度.', '姚信威;张科娜;王万良;陶砾', '碰撞概率;E-Model;语音质量;IEEE802.11e', 'IEEE802.11e无线局域网语音性能分析模型', '小型微型计算机系统, (09), pp 2033-2037, 2012.9.15', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (644, '由于节点的高速移动和拓扑的快速变化,使得在车载自组网中传输时延敏感的数据是一个很大的挑战。针对此问题,提出了一种在机会路由上使用基于滑动窗口的网络编码传输策略。该策略根据网络状态自适应地调整滑动窗口的大小,来编码不同长度的编码包,去容忍ACK的延迟,使得在各种网络条件下都能保持较高的吞吐率;使用下三角形式的渐进编码使接收端逐步解码,从而平滑接收端的解码时间间隔。仿真结果表明,该策略具有更高的吞吐率,同时能够在接收端形成时延抖动小的数据流,为车载自组网中流媒体等时延敏感的数据流传输提供更好的服务质量。', '姚信威;王万良;岑跃峰;李桂森', '滑动窗口;网络编码;机会路由;车载自组网 Slide window,Network coding,Opportunistic routing,VANET', '车载自组网中基于滑动窗口的网络编码传输策略', '计算机科学, (09), pp 85-88+93, 2012', 'UPQ2oUMJesA');
INSERT INTO `search_paper_detail` VALUES (645, '--', '桂元坤;石海燕;濮约庆;马庆;李川;王万良;金亦挺', '', '高校产学研服务平台软件', '2011.6.15, 中国, 中华人民中华人民共和国国家知识产权局, 2011SR048068', 'UPQ2oUMJesA');
