INSERT INTO `search_paper_detail` VALUES (661, '--', '俞凯杰;陈秋迪;濮约庆;马庆;王万良;郑建炜', '', '基于遗传算法的Ogre路径搜索软件', '2011.5.20, 中国, 中华人民中华人民共和国国家知识产权局, 2011SR073454', 'UPQ2oUMJesA');
