INSERT INTO `search_paper_detail` VALUES (1116, '--', '郑红波;陈胜男;王琪;王慧玲;秦绪佳', '', '基于最佳缝合线的序列遥感图像拼接融合方法', '计算机科学, 42(10), pp 306-310, 2015.10.15', 'tXCtpUxTTKa1S3Tu9GOs4g');
